INSERT INTO `destoon_sell_data_5` VALUES('6040','<div style=\"text-align: center;\">&nbsp;<span style=\"color: rgb(255, 0, 0); font-family: 微软雅黑; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; text-align: center; box-sizing: border-box; outline: white; margin: 0px; padding: 0px; border: 0px; font-stretch: inherit;\">产品简介</span><span style=\"color: rgb(255, 0, 0); font-family: 微软雅黑; font-size: 15px; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; text-align: center;\">/产品卖点</span></div>\r\n<p style=\"box-sizing: border-box; outline: white; margin: 0px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 20px; font-family: \'冬青黑体简体中文 W3\', 微软雅黑, 宋体, Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); text-indent: 0px;\"><span style=\"box-sizing: border-box; outline: white; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 15px; line-height: inherit; font-family: 微软雅黑;\">&nbsp;&nbsp;&nbsp;&nbsp;3D灰泥属于水性涂料，其主要成分是聚合物和石英砂，欧美地区广泛用它保护墙体、装饰墙面。3D灰泥的核心优势是环保、防水、美观。</span></p>\r\n<p style=\"box-sizing: border-box; outline: white; margin: 0px; padding: 0px; border: 0px; font-stretch: inherit; font-size: 12px; line-height: 20px; font-family: \'冬青黑体简体中文 W3\', 微软雅黑, 宋体, Arial, Helvetica, sans-serif; color: rgb(51, 51, 51); text-indent: 0px;\"><span style=\"box-sizing: border-box; outline: white; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 15px; line-height: inherit; font-family: 微软雅黑;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"box-sizing: border-box; outline: white; font-weight: inherit; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit;\"><span styl